- type: custom:mushroom-entity-card
  entity: light.salon
  name: Salon
  icon: mdi:lightbulb-on
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: rgba(255, 255, 255, 0.1) !important;
        backdrop-filter: blur(20px) saturate(180%) !important;
        -webkit-backdrop-filter: blur(20px) saturate(180%) !important;
        border: 1px solid rgba(255, 255, 255, 0.3) !important;
        border-radius: 20px !important;
        box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37) !important;
        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
      }
      ha-card:hover {
        transform: translateY(-5px) scale(1.02) !important;
        box-shadow: 0 12px 40px 0 rgba(31, 38, 135, 0.5) !important;
        border-color: rgba(255, 255, 255, 0.5) !important;
        backdrop-filter: blur(30px) saturate(200%) !important;
      }
    script: |
      const card = this;
      const entity = card.querySelector('state-badge');
      if (entity) {
        const state = entity.getAttribute('state');
        if (state === 'on') {
          card.style.setProperty('--card-background', 'rgba(255, 193, 7, 0.2)');
        } else {
          card.style.setProperty('--card-background', 'rgba(255, 255, 255, 0.1)');
        }
      }

- type: custom:mushroom-entity-card
  entity: sensor.temperature_salon
  name: Temp√©rature
  icon: mdi:thermometer
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab) !important;
        background-size: 400% 400% !important;
        animation: gradient-shift 15s ease infinite !important;
        border-radius: 20px !important;
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3) !important;
        color: white !important;
        border: none !important;
      }
      @keyframes gradient-shift {
        0% { background-position: 0% 50%; }
        50% { background-position: 100% 50%; }
        100% { background-position: 0% 50%; }
      }
    script: |
      const card = this;
      const stateBadge = card.querySelector('state-badge');
      if (stateBadge) {
        const temp = parseFloat(stateBadge.getAttribute('state') || 0);
        let gradient;
        if (temp < 15) {
          gradient = 'linear-gradient(-45deg, #4facfe, #00f2fe)'; // Bleu froid
        } else if (temp < 22) {
          gradient = 'linear-gradient(-45deg, #43e97b, #38f9d7)'; // Vert
        } else if (temp < 26) {
          gradient = 'linear-gradient(-45deg, #fa709a, #fee140)'; // Jaune/Rose
        } else {
          gradient = 'linear-gradient(-45deg, #ff6b6b, #ee5a6f)'; // Rouge chaud
        }
        card.style.background = gradient;
      }

- type: custom:mushroom-entity-card
  entity: light.salon
  name: √âclairage
  icon: mdi:lightbulb-on
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: #0a0a0a !important;
        border: 2px solid var(--neon-color, #00ffff) !important;
        border-radius: 15px !important;
        box-shadow: 
          0 0 10px var(--neon-color, #00ffff),
          0 0 20px var(--neon-color, #00ffff),
          0 0 30px var(--neon-color, #00ffff),
          inset 0 0 10px rgba(0, 255, 255, 0.1) !important;
        animation: neon-pulse 2s ease-in-out infinite alternate !important;
        color: var(--neon-color, #00ffff) !important;
      }
      @keyframes neon-pulse {
        from {
          box-shadow: 
            0 0 10px var(--neon-color, #00ffff),
            0 0 20px var(--neon-color, #00ffff),
            0 0 30px var(--neon-color, #00ffff),
            inset 0 0 10px rgba(0, 255, 255, 0.1) !important;
        }
        to {
          box-shadow: 
            0 0 20px var(--neon-color, #00ffff),
            0 0 30px var(--neon-color, #00ffff),
            0 0 40px var(--neon-color, #00ffff),
            0 0 50px var(--neon-color, #00ffff),
            inset 0 0 20px rgba(0, 255, 255, 0.2) !important;
        }
      }
    script: |
      const card = this;
      const stateBadge = card.querySelector('state-badge');
      if (stateBadge) {
        const state = stateBadge.getAttribute('state');
        const neonColors = {
          'on': '#00ff00',
          'off': '#ff0000',
          'unavailable': '#888888'
        };
        const color = neonColors[state] || '#00ffff';
        card.style.setProperty('--neon-color', color);
        card.style.borderColor = color;
        card.style.color = color;
      }

- type: custom:mushroom-entity-card
  entity: sensor.temperature_salon
  name: Temp√©rature
  icon: mdi:thermometer
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%) !important;
        border-radius: 20px !important;
        perspective: 1000px !important;
        transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
        transform-style: preserve-3d !important;
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3) !important;
        color: white !important;
        border: none !important;
      }
      ha-card:hover {
        transform: rotateY(15deg) rotateX(5deg) scale(1.05) !important;
        box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4) !important;
      }
    script: |
      const card = this;
      let mouseX = 0, mouseY = 0;
      card.addEventListener('mousemove', (e) => {
        const rect = card.getBoundingClientRect();
        mouseX = (e.clientX - rect.left - rect.width / 2) / rect.width;
        mouseY = (e.clientY - rect.top - rect.height / 2) / rect.height;
        const rotateY = mouseX * 15;
        const rotateX = -mouseY * 15;
        card.style.transform = `perspective(1000px) rotateY(${rotateY}deg) rotateX(${rotateX}deg) scale(1.05)`;
      });
      card.addEventListener('mouseleave', () => {
        card.style.transform = 'perspective(1000px) rotateY(0deg) rotateX(0deg) scale(1)';
      });

- type: custom:mushroom-entity-card
  entity: sensor.humidite_salon
  name: Humidit√©
  icon: mdi:water-percent
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        position: relative !important;
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%) !important;
        border-radius: 30px !important;
        overflow: hidden !important;
        color: white !important;
        border: none !important;
      }
      ha-card::before {
        content: '' !important;
        position: absolute !important;
        width: 200% !important;
        height: 200% !important;
        background: radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%) !important;
        border-radius: 50% !important;
        top: -50% !important;
        left: -50% !important;
        animation: blob-morph 8s ease-in-out infinite !important;
      }
      @keyframes blob-morph {
        0%, 100% { transform: translate(0, 0) scale(1); border-radius: 50%; }
        25% { transform: translate(20px, -20px) scale(1.1); border-radius: 60% 40% 30% 70%; }
        50% { transform: translate(-20px, 20px) scale(0.9); border-radius: 30% 60% 70% 40%; }
        75% { transform: translate(20px, 20px) scale(1.05); border-radius: 40% 60%; }
      }
    script: |
      const card = this;
      const stateBadge = card.querySelector('state-badge');
      if (stateBadge) {
        const humidity = parseFloat(stateBadge.getAttribute('state') || 0);
        const blob = card.querySelector('::before');
        if (humidity < 30) {
          card.style.setProperty('--blob-color', 'rgba(255, 200, 87, 0.3)');
        } else if (humidity < 60) {
          card.style.setProperty('--blob-color', 'rgba(87, 200, 255, 0.3)');
        } else {
          card.style.setProperty('--blob-color', 'rgba(200, 255, 87, 0.3)');
        }
      }

