- type: custom:mushroom-entity-card
  entity: sensor.temperature_salon
  name: Temp√©rature
  icon: mdi:thermometer
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%) !important;
        border-radius: 20px !important;
        position: relative !important;
        color: white !important;
        border: none !important;
        box-shadow: 0 0 20px rgba(245, 87, 108, 0.5) !important;
        animation: pulse-glow 2s ease-in-out infinite !important;
      }
      @keyframes pulse-glow {
        0%, 100% {
          box-shadow: 0 0 20px rgba(245, 87, 108, 0.5), 0 0 40px rgba(245, 87, 108, 0.3) !important;
          transform: scale(1) !important;
        }
        50% {
          box-shadow: 0 0 30px rgba(245, 87, 108, 0.8), 0 0 60px rgba(245, 87, 108, 0.5) !important;
          transform: scale(1.02) !important;
        }
      }
