      const card = this;
      const stateBadge = card.querySelector('state-badge');
      if (stateBadge) {
        const temp = parseFloat(stateBadge.getAttribute('state') || 0);
        let pulseSpeed = '2s';
        let glowIntensity = '0.5';
        if (temp > 25) {
          pulseSpeed = '1s';
          glowIntensity = '0.8';
          card.style.background = 'linear-gradient(135deg, #ff6b6b 0%, #ee5a6f 100%)';
        } else if (temp < 15) {
          pulseSpeed = '3s';
          glowIntensity = '0.3';
          card.style.background = 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)';
        }
        card.style.setProperty('--pulse-speed', pulseSpeed);
        card.style.setProperty('--glow-intensity', glowIntensity);
      }

- type: custom:mushroom-entity-card
  entity: sensor.temperature_salon
  name: Temp√©rature
  icon: mdi:thermometer
  layout: horizontal
  primary_info: name
  secondary_info: state
  card_mod:
    style: |
      ha-card {
        background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%) !important;
        border-radius: 20px !important;
        transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1) !important;
        color: white !important;
        border: none !important;
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2) !important;
      }
      ha-card:hover {
        transform: scale(1.05) translateY(-5px) !important;
        box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3) !important;
      }
    script: |
      const card = this;
      let isHovering = false;
      card.addEventListener('mouseenter', () => {
        isHovering = true;
        card.style.cursor = 'pointer';
      });
      card.addEventListener('mousemove', (e) => {
        if (isHovering) {
          const rect = card.getBoundingClientRect();
          const x = e.clientX - rect.left - rect.width / 2;
          const y = e.clientY - rect.top - rect.height / 2;
          const rotateX = (y / rect.height) * 10;
          const rotateY = (x / rect.width) * -10;
          card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale(1.05) translateY(-5px)`;
        }
      });
      card.addEventListener('mouseleave', () => {
        isHovering = false;
        card.style.transform = 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale(1) translateY(0px)';
      });

- type: custom:mushroom-entity-card
  entity: sensor.temperature_salon
  name: Temp√©rature
