      const card = this;
      const stateBadge = card.querySelector('state-badge');
      if (stateBadge) {
        const temp = parseFloat(stateBadge.getAttribute('state') || 0);
        let gradient;
        if (temp < 10) {
          gradient = 'linear-gradient(135deg, #1e3c72 0%, #2a5298 100%)'; // Bleu fonc√©
        } else if (temp < 18) {
          gradient = 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'; // Bleu clair
        } else if (temp < 22) {
          gradient = 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)'; // Vert
        } else if (temp < 26) {
          gradient = 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)'; // Jaune/Rose
        } else if (temp < 30) {
          gradient = 'linear-gradient(135deg, #ff9a56 0%, #ff6a88 100%)'; // Orange/Rose
        } else {
          gradient = 'linear-gradient(135deg, #ff6b6b 0%, #ee5a6f 100%)'; // Rouge
