- type: entity
  entity: sensor.portainer_status_docker
  name: Portainer Docker (visible si on)
  icon: |
    {% if is_state('sensor.portainer_status_docker', 'running') %}
      mdi:docker
    {% elif is_state('sensor.portainer_status_docker', 'stopped') %}
      mdi:docker-off
    {% else %}
      mdi:help-circle
    {% endif %}
  state_color: true

- type: markdown
  content: |
    {% set state = states('sensor.portainer_status_docker') %}
    {% set color = 'success' if state == 'running' else 'error' if state == 'exited' else 'warning' %}
    <ha-card>
      <div class="container {{ color }}">
        <ha-icon icon="
          {% if state == 'running' %}mdi:docker
          {% elif state == 'exited' %}mdi:alert-circle
          {% else %}mdi:help-circle{% endif %}"
        ></ha-icon>
        <div class="state">{{ state }}</div>
      </div>
    </ha-card>
  style: |
    ha-card {
      padding: 16px;
      text-align: center;
    }
    .container {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding: 16px;
      border-radius: 8px;
    }
    .success {
      background: var(--success-color);
      color: var(--text-primary-color);
    }
    .error {
      background: var(--error-color);
      color: var(--text-primary-color);
    }
    .warning {
      background: var(--warning-color);
      color: var(--text-primary-color);
    }
    ha-icon {
      --mdc-icon-size: 48px;
      margin-bottom: 16px;
    }
    .state {
      font-size: 1.2rem;
      font-weight: bold;
      text-transform: capitalize;
    }

